// src/pages/Dashboard1.jsx
import React, { useState } from "react";
import Template from "../components/Template";
import Header from "../components/Header";
import Sidebar from "../components/Sidebar";
import Card from "../components/Card";

export default function Dashboard1() {
  // 상위 메뉴 상태 (Header 메뉴 클릭 시 변경)
  const [activeMenu, setActiveMenu] = useState("Dashboard");

  // 진행률 상태 (동적)
  const [progress, setProgress] = useState(64); // % 단위

  return (
    <Template>
      {/* Header */}
      <Header activeMenu={activeMenu} setActiveMenu={setActiveMenu} />

      <div className="flex w-full mt-6">
        {/* Sidebar (Frame 94) - Dashboard 선택 시만 렌더링 */}
        {activeMenu === "Dashboard" && <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />}

        {/* 메인 컨텐츠 영역 */}
        <main className="flex-1 p-6 flex flex-col gap-6">
          {/* 진행률 텍스트 */}
          <div
            className="text-[28px] font-semibold"
            style={{
              fontFamily: "Pretendard",
              lineHeight: "140%",
              letterSpacing: "-0.025em",
              color: "#5F43FF",
            }}
          >
            {progress}%
          </div>

          {/* 진행률 바 */}
          <div className="w-[500px] h-[15px] rounded-[12px] bg-[#EBE9FB] shadow-inner relative">
            <div
              className="h-full rounded-[12px]"
              style={{
                width: `${progress}%`,
                background: "linear-gradient(90deg, #5F43FF 0%, #8243FF 100%)",
                boxShadow: "0.5px 0.5px 1px 0px #11007426 inset",
              }}
            ></div>
          </div>

          {/* 설명 텍스트 */}
          <p
            className="text-[20px] font-medium"
            style={{
              fontFamily: "Pretendard",
              lineHeight: "140%",
              letterSpacing: "-0.025em",
            }}
          >
            분석이 끝나면 자동으로 결과가 로드됩니다.
          </p>

          {/* 카드(Grid) */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            <Card title="Resume Templates" subtitle="대시보드 내용" user="User1234" status="ON" />
            <Card title="Dashboard" subtitle="데이터 확인" user="User5678" status="OFF" />
            <Card title="Upload" subtitle="업로드 상태" user="User9999" status="ON" />
          </div>
        </main>
      </div>
    </Template>
  );
}
