// src/pages/Dashboard1.jsx
import React from 'react';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';

/**
 * isAnalyzing 상태에 따라 다른 컨텐츠를 보여주는 컴포넌트
 */
function AnalysisContent({ isAnalyzing }) {
  // 1. 분석이 시작되지 않은 경우
  if (!isAnalyzing) {
    return (
      <div className="flex flex-col items-center justify-center h-full gap-4 text-center">
        <span className="text-2xl font-medium text-gray-500">
          분석을 시작해주세요
        </span>
        <span className="text-gray-400">
          Upload 페이지에서 파일을 올리고 '분석 시작하기' 버튼을 눌러주세요.
        </span>
      </div>
    );
  }

  // 2. 분석이 진행 중인 경우 (이미지 참고)
  return (
    <div className="flex flex-col items-center justify-center h-full gap-4">
      <span className="text-xl font-medium text-gray-700">
        분석이 끝나면 자동으로 결과가 로드됩니다.
      </span>
      {/* 프로그레스 바 */}
      <div className="w-full max-w-md bg-gray-200 rounded-full h-2.5">
        <div 
          className="bg-[#1AC0A4] h-2.5 rounded-full" 
          style={{ width: '64%' }} // 이미지의 64% 반영
        ></div>
      </div>
      <span className="text-2xl font-bold text-[#1AC0A4]">
        64%
      </span>
    </div>
  );
}


/**
 * 대시보드 메인 페이지 (헤더 + 사이드바 + 컨텐츠)
 * App.js로부터 isAnalyzing 상태를 props로 전달받습니다.
 */
export default function Dashboard1({ isAnalyzing }) {
  return (
    <div className="w-full min-h-screen bg-white">
      <Header />
      <Sidebar />
      
      {/* 메인 컨텐츠 영역 */}
      {/* 사이드바 너비(w-60 -> 15rem)만큼 왼쪽 패딩, 헤더 높이(68px)만큼 위쪽 패딩 */}
      <main className="pl-60 pt-[68px]">
        {/* 화면 높이에서 헤더 높이를 뺀 만큼의 컨텐츠 영역 */}
        <div className="w-full h-[calc(100vh-68px)] p-8">
          <AnalysisContent isAnalyzing={isAnalyzing} />
        </div>
      </main>
    </div>
  );
}
