import React, { useState } from "react"; // <-- useState 임포트
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

// 페이지들 import
import LoginPage from "./pages/LoginPage";
import MainPage from "./pages/MainPage";
import Dashboard1 from "./pages/Dashboard1"; // <-- Dashboard1 임포트
import Upload from "./pages/Upload";
import ResumeTemplates from "./pages/ResumeTemplates";

// 글로벌 스타일
import "./styles/globals.css";
// import "./styles/variables.css"; // globals.css로 합쳤거나 사용하지 않는다면 주석 처리

export default function App() {

  // '분석 중' 상태를 App.js (최상위)로 끌어올림
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  return (
    <Router>
      <Routes>
        {/* 기본 진입 시 로그인 페이지로 이동 */}
        <Route path="/" element={<Navigate to="/login" />} />

        {/* 로그인 페이지 */}
        <Route path="/login" element={<LoginPage />} />

        {/* 메인 페이지 */}
        <Route path="/main" element={<MainPage />} />

        {/* Upload 페이지 (상태 변경 함수를 props로 전달) */}
        <Route 
          path="/upload" 
          element={<Upload setIsAnalyzing={setIsAnalyzing} />} 
        />

        {/* Resume Templates 페이지 */}
        <Route path="/resume" element={<ResumeTemplates />} />

        {/* Dashboard1 페이지 (상태값을 props로 전달) */}
        <Route 
          path="/dashboard" 
          element={<Dashboard1 isAnalyzing={isAnalyzing} />} 
        />

        {/* 잘못된 경로 접근 시 기본 페이지로 */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}
