// src/components/Header.jsx
// 각 대시보드에서 고정된 부분.
import React from "react";
import { useNavigate } from "react-router-dom";

export default function Header() {
  const navigate = useNavigate();

  return (
    <header className="fixed top-0 left-0 w-full bg-white shadow-sm z-50 h-20 flex items-center">
      <div className="container mx-auto flex items-center justify-between px-8">
        
        {/* 1. 로고 + 네비게이션 (왼쪽 그룹) - Fix 1: 위치 수정 */}
        <div className="flex items-center gap-10"> {/* 로고와 네비 간격 */}
          
          {/* 로고 */}
          <div className="flex items-center gap-1 cursor-pointer" onClick={() => navigate("/main")}>
            <span className="text-2xl font-bold text-gray-800">FITURE</span>
            <div className="w-2.5 h-2.5 bg-[#1AC0A4] rounded-full"></div>
          </div>

          {/* 네비게이션 링크 */}
          <nav className="flex gap-8"> {/* 링크 간 간격 */}
            <span 
              onClick={() => navigate("/upload")} 
              className="text-gray-600 hover:text-gray-900 cursor-pointer"
            >
              upload
            </span>
            <span 
              onClick={() => navigate("/dashboard")} 
              className="text-gray-600 hover:text-gray-900 cursor-pointer"
            >
              dashboard
            </span>
            <span 
              onClick={() => navigate("/resume")} 
              className="text-gray-600 hover:text-gray-900 cursor-pointer"
            >
              resume templates
            </span>
          </nav>
        </div>

        {/* 2. 로그인 버튼 (오른쪽) */}
        <button
          onClick={() => navigate("/login")}
          className="px-5 py-2 bg-[#1AC0A4] text-white rounded-md font-medium hover:bg-opacity-80 transition-colors"
        >
          Sign in
        </button>
      </div>
    </header>
  );
}
