// Frame 94

import React, { useState } from "react";

export default function Sidebar({ activeMenu, setActiveMenu }) {


  const menuItems = [
    { label: "분석 결과" },
    { label: "지원자 비교" },
    { label: "이전 데이터" },
  ];

  return (
    <aside className="w-56 h-screen bg-[#D9D9D9] flex flex-col py-6 px-4 font-[Pretendard] font-medium text-[16px]">
      <div className="flex flex-col gap-6 mt-8">
        {menuItems.map((item) => {
          const isActive = activeMenu === item.label;

          return (
            <button
              key={item.label}
              onClick={() => setActiveMenu(item.label)}
              className={`flex items-center gap-3 px-2 py-2 rounded-xl transition-colors ${
                activeMenu === item.label ? "bg-[#EBE9FB] shadow-inner" : "hover:bg-white/40"
              }`}
            >
              {/* 원형 아이콘 */}
            <div
            className={`rounded-full border-[1.5px] ${
                isActive
                ? "w-[15px] h-[15px] bg-[var(--primary-color)] border-[var(--primary-color)]"
                : "w-[13.93px] h-[15px] bg-[var(--gray-medium)] border-[var(--gray-dark)]"
            }`}
            />

              {/* 텍스트 */}
              <span>{item.label}</span>
            </button>
          );
        })}
      </div>
    </aside>
  );
}
