// src/pages/Upload1.jsx
import React, { useState } from "react";
import Template from "../components/Template";
import Header from "../components/Header";
import Sidebar from "../components/Sidebar";
import IconSet from "../components/IconSet";

export default function Upload1() {
  const [activeMenu, setActiveMenu] = useState("Upload");
  const [uploading, setUploading] = useState(false);
  const [uploadedFiles, setUploadedFiles] = useState([]);

  // 파일 선택/업로드 핸들러
  const handleFileUpload = (e) => {
    const files = Array.from(e.target.files);
    const newFiles = files.map((file) => ({
      name: file.name,
      id: Math.random().toString(36).substr(2, 9),
    }));
    setUploadedFiles((prev) => [...prev, ...newFiles]);
  };

  // 분석 시작 버튼 클릭
  const handleStartAnalysis = () => {
    setUploading(true);
    // 시뮬레이션: 2초 후 완료
    setTimeout(() => {
      alert("분석이 시작되었습니다. 진행 상황은 Dashboard에서 확인하세요.");
    }, 500);
  };

  return (
    <Template>
      <Header activeMenu={activeMenu} setActiveMenu={setActiveMenu} />

      <div className="flex w-full mt-6">
        {activeMenu === "Dashboard" && <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />}

        <main className="flex-1 p-6 flex flex-col gap-8 items-center">
          <div className="text-[20px] font-medium text-center mb-4" style={{ fontFamily: "Pretendard", lineHeight: "140%", letterSpacing: "-0.025em" }}>
            지정된 필터 목록 표시
          </div>

          {/* Rectangle 14 */}
          <div className="w-[1092px] h-[157px] bg-[#D9D9D9] rounded-md mb-6"></div>

          {/* Frame114: 필터 박스 */}
          <div className="w-[214px] h-[70px] bg-[#EBEBF1] rounded-lg flex items-center justify-center gap-2 p-[21px_66px] mb-6">
            <div className="flex items-center gap-2">
              <IconSet name="Sliders" />
              <span>필터</span>
            </div>
          </div>

          {/* Frame19: 파일 업로드 영역 */}
          <div
            className="w-[1330px] h-[500px] rounded-[40.5px] bg-[#EBEBF5] shadow-inner flex flex-col items-start p-4 gap-4 overflow-y-auto"
            style={{
              boxShadow: "-2px -2px 4px #FFFFFF inset, 2px 2px 3px #11007426 inset",
            }}
          >
            <label
              className="w-full h-[100px] border border-black rounded-lg flex items-center justify-center cursor-pointer"
            >
              <input type="file" className="hidden" multiple onChange={handleFileUpload} />
              <div className="flex items-center gap-2">
                <IconSet name="Plus" />
                <span>파일 업로드</span>
              </div>
            </label>

            <p>또는 마우스로 드래그하기</p>

            {/* 업로드된 파일 리스트 */}
            {uploadedFiles.map((file) => (
              <div
                key={file.id}
                className="w-[1165px] h-[40px] bg-white rounded-md flex items-center gap-2 px-3"
              >
                <IconSet name="File" />
                <span>{file.name}</span>
              </div>
            ))}
          </div>

          {/* Frame118: 분석 시작 버튼 */}
          <button
            onClick={handleStartAnalysis}
            className={`w-[1330px] h-[70px] text-white rounded-lg text-[20px] font-medium mt-4 transition-colors ${
              uploading ? "bg-[#5F43FFB2]" : "bg-[#5F43FF]"
            }`}
            style={{ fontFamily: "Pretendard", lineHeight: "140%", letterSpacing: "-0.025em" }}
          >
            {uploading ? "분석 중입니다. 진행 상황은 Dashboard에서 확인하세요." : "분석 시작하기"}
          </button>
        </main>
      </div>
    </Template>
  );
}
