// src/pages/MainPage.jsx
import React from "react";
import Template from "../components/Template";
import Header from "../components/Header";

export default function MainPage() {
  return (
    <Template>
      {/* Header 항상 보임 */}
      <Header activeMenu="Main" />

      <main className="relative w-full h-screen flex items-center justify-center bg-white overflow-hidden">
        {/* 배경 원들 */}
        <div
          className="absolute rounded-full"
          style={{
            width: "700px",
            height: "700px",
            left: "1156px",
            background: "radial-gradient(50% 50% at 50% 50%, rgba(208, 252, 236, 0.6475) 0%, rgba(255, 255, 255, 0.925) 100%)",
            mixBlendMode: "multiply",
          }}
        ></div>
        <div
          className="absolute rounded-full"
          style={{
            width: "750px",
            height: "750px",
            top: "129px",
            left: "791px",
            background: "radial-gradient(50% 50% at 50% 50%, rgba(219, 208, 252, 0.6475) 0%, rgba(255, 255, 255, 0.925) 100%)",
            mixBlendMode: "multiply",
          }}
        ></div>

        {/* 로고 자리 */}
        <div className="absolute w-[Rectangle 13 width?px] h-[Rectangle 13 height?px] bg-gray-200 rounded-md">
          로고
        </div>

        {/* 텍스트 컨텐츠 */}
        <div className="relative z-10 flex flex-col items-start gap-4 max-w-[800px] text-left">
          {/* Frame 119 */}
          <h1
            className="text-black font-medium"
            style={{
              width: "800px",
              height: "180px",
              fontFamily: "Pretendard",
              fontSize: "28px",
              lineHeight: "140%",
              letterSpacing: "-0.025em",
            }}
          >
            복잡한 채용 과정을 더 쉽게, 더 빠르게
          </h1>

          {/* Frame 170 */}
          <p className="text-[#767676] text-[20px] font-medium leading-[140%]" style={{ fontFamily: "Pretendard" }}>
            간단한 프로세스로 수많은 이력서를 한번에 <br />
            기업이 원하는 직관적인 ATS
          </p>
        </div>
      </main>
    </Template>
  );
}
