// src/components/Header.jsx
// 각 대시보드에서 고정된 부분.
import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Header() {
  const navigate = useNavigate();

  return (
    <header className="fixed top-0 left-0 w-full h-20 bg-white/90 backdrop-blur-md shadow-sm z-50">
      <div className="container mx-auto h-full flex items-center justify-between px-8">
        
        {/* 1. 로고 */}
        <Link to="/main" className="flex items-center gap-2">
          <span className="text-xl font-bold tracking-tight text-gray-800">FITURE</span>
          <div className="w-2.5 h-2.5 bg-[#1AC0A4] rounded-full"></div>
        </Link>

        {/* 2. 네비게이션 링크 */}
        <nav className="flex items-center gap-8">
          <Link to="/upload" className="text-gray-600 hover:text-gray-900">
            upload
          </Link>
          <Link to="/dashboard" className="text-gray-600 hover:text-gray-900">
            dashboard
          </Link>
          <Link to="/resume" className="text-gray-600 hover:text-gray-900">
            resume templates
          </Link>
        </nav>

        {/* 3. 로그인 버튼 */}
        <button
          onClick={() => navigate("/login")}
          className="px-6 py-2.5 border-5 border-[#1AC0A4] text-[#1AC0A4] font-medium rounded-md hover:bg-[#1AC0A4] hover:text-white transition-colors"
        >
          Sign in
        </button>


      </div>
    </header>
  );
}
