// src/components/Header.jsx
// 각 대시보드에서 고정된 부분. Frame 81
// src/components/Header.jsx
import React from "react";

export default function Header({ activeMenu, setActiveMenu }) {
  return (
    <header
      className="w-full flex justify-between items-center px-8 py-4 bg-white border-b border-gray-200"
      style={{
        fontFamily: "Pretendard",
        fontWeight: 500,
        letterSpacing: "-0.025em",
        lineHeight: "140%",
      }}
    >
      {/* 좌측: 네비게이션 메뉴 */}
      <nav className="flex gap-8 text-[16px]">
        <button
          onClick={() => setActiveMenu("Resume Templates")}
          className={activeMenu === "Resume Templates" ? "text-[#5F43FF]" : "hover:text-[#5F43FF] transition-colors"}
        >
          Resume Templates
        </button>

        <button
          onClick={() => setActiveMenu("Dashboard")}
          className={activeMenu === "Dashboard" ? "text-[#5F43FF]" : "hover:text-[#5F43FF] transition-colors"}
        >
          Dashboard
        </button>

        <button
          onClick={() => setActiveMenu("Upload")}
          className={activeMenu === "Upload" ? "text-[#5F43FF]" : "hover:text-[#5F43FF] transition-colors"}
        >
          Upload
        </button>
      </nav>

      {/* 우측: 사용자 정보 */}
      <div className="flex items-center gap-6">
        {/* Hirely 로고 */}
        <div className="flex items-center gap-2">
          <span className="bg-gradient-to-r from-[var(--primary-color)] to-[var(--secondary-color)] text-[var(--white)] px-3 py-1 rounded-xl text-[18px]">
            Hirely
          </span>
          <span className="bg-[#5F43FF] text-white text-[13px] px-2 py-0.5 rounded-md">ON</span>
        </div>

        {/* 사용자 */}
        <div className="flex items-center gap-2">
          <div className="w-8 h-8 bg-gray-300 rounded-full" />
          <span className="text-[14px] text-gray-800">User1234</span>
        </div>
      </div>
    </header>
  );
}
