import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
// 템플릿 파일 경로를 소문자 't'로 수정했습니다.
import Template from "../components/Template";

export default function LoginPage() {
  const navigate = useNavigate(); // ← 컴포넌트 안에서 useNavigate 사용
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    console.log(`로그인 시도: ${email} / ${password}`);
    navigate("/main");
  };

  return (
    <Template>
      {/* Tailwind 클래스가 적용된 main 요소.
        Template 컴포넌트가 이 children을 렌더링합니다.
      */}
      <main className="w-full h-screen flex items-center justify-center bg-gray-50">
        
        {/* 로그인 폼 컨테이너 */}
        <div className="w-full max-w-md bg-white rounded-xl shadow-lg p-8">
          
          {/* LOGIN 타이틀 */}
          <h1 className="text-3xl font-bold text-center text-[#5F43FF] mb-8">
            LOGIN
          </h1>

          {/* 입력 폼 */}
          <form className="flex flex-col gap-6" onSubmit={handleLogin}>
            
            {/* 아이디 */}
            <div className="flex flex-col gap-2">
              <label className="text-gray-700 font-medium">아이디</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="address123@gmail.com"
                className="w-full h-12 px-4 rounded-md border border-gray-300 bg-gray-100 focus:outline-none focus:ring-2 focus:ring-[#5F43FF]"
              />
            </div>

            {/* 패스워드 */}
            <div className="flex flex-col gap-2">
              <label className="text-gray-700 font-medium">패스워드</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="*********"
                className="w-full h-12 px-4 rounded-md border border-gray-300 bg-gray-100 focus:outline-none focus:ring-2 focus:ring-[#5F43FF]"
              />
            </div>

            {/* 로그인 버튼 */}
            <button
              type="submit"
              className="w-full h-12 mt-4 bg-[#5F43FF] text-white font-medium rounded-md hover:bg-[#4a32cc] transition-colors"
            >
              로그인
            </button>
          </form>

        </div>
      </main>
    </Template>
  );
}
